package cn.uc.yiqibang.beans;

public class Admin {

	
	

	private int id;
	private int userId;
	private boolean state;
	private int level;
	
	public Admin() {
		// TODO Auto-generated constructor stub
	}

	public Admin(int userId, boolean state, int level) {
		super();
		this.userId = userId;
		this.state = state;
		this.level = level;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public int getUserId() {
		return userId;
	}

	public void setUserId(int userId) {
		this.userId = userId;
	}

	public boolean isState() {
		return state;
	}

	public void setState(boolean state) {
		this.state = state;
	}

	public int getLevel() {
		return level;
	}

	public void setLevel(int level) {
		this.level = level;
	}
	
}
